if msg == "" then
	self:SetText(CEPGP_standby_whisper_msg);
elseif CEPGP_standby_whisper_msg ~= msg then
	CEPGP_standby_whisper_msg = msg;
	CEPGP_print("Whisper message for standby EP updated");
end

Scripts.OnShow:
